mapper.select:
SELECT bb_no, bb_postNum, bb_bm_nickname, bb_title, bb_content, bb_date, bb_readCount
FROM bizone_board
WHERE bb_no = #{bb_no}

mapper.update:
UPDATE bizone_board
SET bb_readCount = bb_readCount + 1
WHERE bb_no = #{bb_no}

UPDATE bizone_board
SET bb_title = #{bb_title},
    bb_content = #{bb_content}
WHERE bb_no = #{bb_no}

UPDATE bizone_board
SET bb_postNum = (@rownum := @rownum + 1)
    ORDER BY bb_no;
